declare module "*.css" {
  const content: string;
  export default content;
}

declare module "*.svg" {
  const content: string;
  export default content;
}

declare module "*.toml" {
  const content: object;
  export default content;
}

declare module "*.nu" {
  const content: string;
  export default content;
}

declare module "*.yaml" {
  const content: any;
  export default content;
}

declare module "*.yml" {
  const content: any;
  export default content;
}
